(763, '_transient_plapi_draft_core_raw', 'a:2:{s:8:"sections";s:56403:".section-mediabox {\n	&.no-pad .pl-section-pad{\n		padding: 0;\n	}\n	.mediabox-wrap{\n		min-height: 50px;\n	//	overflow: hidden;\n		.background-size(cover);\n		\n	}\n\n	img,\n	iframe,\n	object, \n	embed{\n		max-width: 100%;\n		max-height: 100%;\n	}\n	\n	\n	\n}.section-navbar {\n	margin: 0 auto;\n	width: 100%;\n	.content-pad,\n	.pl-section-pad{\n		padding-top: 5px;\n		padding-bottom: 5px;\n		.navbar-content-pad{\n			padding: 0 15px;\n		}\n	}\n	&.pl-area .navbar{\n		border: none;\n	}\n	.navbar{\n		\n		color:rgba(255,255,255,.7);\n\n		&.navbar-content-width{\n			.border-radius( 3px );\n		}\n		&.plnav_hover {\n			.sub-menu .dropdown-menu {\n				position: absolute;\n				top: 0;\n				left: 100%;\n				margin-top: -1px;\n				visibility: hidden;\n			}\n			.dropdown-menu li:hover .sub-menu {\n			  	visibility: visible;\n			  	margin-top:-6px;\n			}\n			.dropdown:hover .dropdown-menu {\n				margin-top:0;\n			  	display: block;\n			}\n		}\n		.navline {\n			float: left;\n			position: relative;\n			left: 0;\n			display: block;\n			margin: 0 10px 0 0;\n			font-size: .9em;\n\n			> .dropdown > .dropdown-menu{\n				&:before {\n				  content: '''';\n				  display: inline-block;\n				  border-left: 7px solid transparent;\n				  border-right: 7px solid transparent;\n				  border-bottom: 7px solid #ccc;\n				  border-bottom-color: rgba(0, 0, 0, 0.2);\n				  position: absolute;\n				  top: -7px;\n				  left: 9px;\n				}\n				&:after {\n				  content: '''';\n				  display: inline-block;\n				  border-left: 6px solid transparent;\n				  border-right: 6px solid transparent;\n				  border-bottom: 6px solid #ffffff;\n				  position: absolute;\n				  top: -6px;\n				  left: 10px;\n				}\n			}\n			.caret{\n				border-top-color:  rgba(255,255,255,.5);\n			}\n			&.pull-right {\n				float: right;\n				position: relative;\n				> li:last-child .dropdown-menu{\n					left: auto;\n					right: 0;\n					&:before{\n						left: auto;\n						right: 12px;\n					}\n					&:after{\n						left: auto;\n						right: 13px;\n					}\n					.dropdown-submenu > a:after {\n					  	border-width: 5px 5px 5px 0;\n						border-right-color: darken(@dropdownBackground, 20%);\n					}\n					.dropdown-menu {\n						left: -100%;\n						right: auto;\n						margin-left: 0;\n						margin-right: -1px;\n						.border-radius(6px 0 6px 6px);\n					}\n				}\n\n			}\n\n\n			> li {\n				display: block;\n				float: left;\n\n				> a {\n				\n					\n					.transition(all .3s ease-in-out);\n					float: none;\n					display: block;\n					padding: 9px 12px;\n					line-height: 19px;\n					color:#fff; /* IE */\n					color:rgba(255,255,255,.8);\n					text-decoration: none;\n					&:hover{\n						background-color: transparent;\n						color:rgba(255,255,255,.9);\n						text-decoration: none;\n					}\n				}\n				&.dropdown.open > a{\n					color:#fff;\n					background: rgba(0, 0, 0, 0.1);\n				}\n				&.current-menu-item > a {\n				  	background-color: rgba(0,0,0,.1);\n					color:#ffffff;\n					text-decoration: none;\n				}\n				&.active > a:hover {\n					color:rgba(255,255,255,.9);\n					  text-decoration: none;\n					  background-color: #222222;\n				}\n			}\n		}\n\n		.searchform{\n			float: right;\n			margin: 6px 0 5px;\n			width: auto;\n			max-width: 190px;\n			.transition( margin .3s ease-in-out);\n			&.has-focus{\n				color: blue;\n				i{color: #666;}\n				.searchfield{\n					background-color:#fff;\n					color:#000;\n					width: 140px;\n				}	\n			}\n			.searchfield{\n				border:none;\n				background-color:rgba(255,255,255,.1);\n				width: 24px;\n				\n			}\n		}\n		.plbrand{\n			float:left;\n\n			margin: 4px 2% 0 0;\n			padding: 0 1%;\n			line-height: 15px;\n			.plbrand-text{\n				font-size: 19px;\n				line-height: 30px;\n				margin: 0;\n			}\n			img{\n				max-height: 29px;\n				.transition( max-height .3s ease-in-out);\n			}\n		}\n\n		&.pl-color-black-trans{\n			background: #000;\n			\n			.navline{\n				> li{\n					&:hover,\n					&.current-menu-item,\n					&.current_page_item,\n					&.dropdown.open{\n						> a{\n							background: #333;\n						}\n					}\n\n				}\n			}\n		}\n		&.pl-color-red{\n			background: @btnImportantBackground;\n			.navline{\n				> li{\n					&:hover,\n					&.current-menu-item,\n					&.current_page_item,\n					&.dropdown.open{\n						> a{\n							background: darken(@btnImportantBackground, 10%);\n						}\n					}\n\n				}\n			}\n		}\n		&.pl-color-blue{\n			\n			background: @btnPrimaryBackground;\n			.navline{\n				> li{\n					&:hover,\n					&.current-menu-item,\n					&.current_page_item,\n					&.dropdown.open{\n						> a{\n							background: darken(@btnPrimaryBackground, 10%);\n						}\n					}\n\n				}\n			}\n		}\n		&.pl-color-orange{\n			background: @btnWarningBackground;\n			.navline{\n				> li{\n					&:hover,\n					&.current-menu-item,\n					&.current_page_item,\n					&.dropdown.open{\n						> a{\n							background: darken(@btnWarningBackground, 10%);\n						}\n					}\n\n				}\n			}\n		}\n\n		&.pl-color-base{\n			background: @pl-base;\n			color: @pl-text;\n			.searchform .searchfield{\n				background-color: @baseContrastSlight;\n				\n			}\n			.navline{\n				> li{\n					> a {\n						.transition(all 0.1s linear);\n						color: fadeout(@textColor, 40%);\n						text-shadow: none;\n\n					}\n					&:hover > a,\n					&.current-menu-item > a,\n					&.dropdown.open > a{\n						color: @textColor;\n						background-color: @pl-base;\n						box-shadow: inset 0 -4px 0 @linkColor;\n					}\n\n				}\n				.caret{\n					border-top-color: fadeout(@textColor, 40%);\n				}\n			}\n		}\n\n		&.pl-color-grey{\n			background: #EEE;\n			color: rgba(0,0,0,.5);\n			.searchform .searchfield{\n			\n				background-color: #ddd;\n			}\n			.navline{\n				> li{\n					&:hover,\n					&.current-menu-item, \n					&.current_page_item,\n					&.active {\n						> a{\n							background: #e1e1e1;\n						}\n					}\n					> a {\n						color:rgba(0,0,0,.8);\n					\n					}\n				\n					&.active > a:hover {\n						color:rgba(0,0,0,.8);\n						background-color: #ddd;\n					}\n					&.dropdown.open > a{\n						\n						color:#000;\n						background: rgba(0, 0, 0, 0.1);\n					}\n				}\n				.caret{\n					border-top-color:  rgba(0,0,0,.5);\n				}\n			}\n\n\n			.nav-btn-navbar .icon-bar{\n				background:rgba(0,0,0,.7);\n				box-shadow: 0 1px 0 #fff;\n			}\n		}\n	}\n	\n}\n\n// Dont nest, needs to be minimum specificity\n.pldrop ul{\n	display: none;\n}\n\n\n/* Buttons with Dropdowns in Posts */\n.hentry .btn-group ul {\n	margin:0;\n	li {\n		list-style:none;\n	}\n}\n\n/* Nav Toggle Responsive\n ************************************************************/\n	.nav-btn-navbar {\n		color: inherit;\n		display: none;\n		float: right;\n		position: relative;\n		z-index: 50;\n		padding: 2px 10px;\n		margin: 5px;\n		.border-radius(2px);\n		#gradient > .vertical(rgba(255,255,255,.1), rgba(0,0,0, .08));\n		border: 1px solid rgba(0,0,0,.25);\n		border-left: 1px solid rgba(0,0,0,.17);\n		border-top: 1px solid rgba(0,0,0,.17);\n		box-shadow: inset 1px 1px 0 rgba(255, 255, 255, 0.3);\n		&:hover,\n		&:active,\n		&.active,\n		&.disabled,\n		&[disabled] {\n		  	color: inherit; \n			opacity: .7;\n		}\n		&:active,\n		&.active {\n			box-shadow: inset 1px 1px 0 rgba(0, 0, 0, 0.3);\n		  	background-color: rgba(0,0,0,.1);\n		}\n		\n	}\n\n.navbar,\n.navbar-inner,\n.navbar-collapse{\n	height:auto;\n	overflow: visible;// IE8 FIx\n}\n.nav-collapse.collapse {\n	position: relative;\n	height: auto; // this was height:0; but testing height: auto for IE8\n	overflow: hidden;\n	.transition(height 0.35s ease);\n	&.in{\n		height: auto;\n	}\n}\n\n\n\n// ************************************************************/\n// * Responsive Handling\n// ************************************************************/\n@media (max-width: 400px) {\n\n.navbar .plbrand img{\n	max-width: 210px;\n}\n\n}//-- End Media Query --//\n\n@media (max-width: 600px) {\n\n.navbar.navbar-content-width {\n	width: 95%;\n	margin:0 auto;\n}\n\n}//-- End Media Query --//\n\n\n@media (max-width: @resPortraitTablet){\n\n\n#site .navbar{\n	.brand {\n	    padding-left: 10px;\n	    padding-right: 10px;\n	    margin: 0 0 0 -5px;\n	  }\n	.searchform{\n		float: none;\n		padding: 10px;\n	}\n\n\n	.nav-collapse {\n		overflow: hidden;\n		height: 0;\n		clear: both;\n	}\n\n\n	.nav-btn-navbar {\n		display: inline-block;\n	}\n\n}\n\n} //-- End Media Query --//\n\n@media (min-width: @resPortraitTablet + 1) {\n\n	.nav-collapse.collapse {\n		height: auto !important;\n		overflow: visible !important;\n	}\n\n}\n\n\n.pl-watermark{\n	text-align: center; \n	.stack-element{\n		margin-bottom: 8px;\n	}\n	\n	.fb-like-box span{ display: block; line-height:0; padding:0; }\n	\n	.powered-by{\n		text-align: center;\n	\n		font-size: 10px;\n	}\n	\n	.the-watermark {\n		text-align: center;\n		padding: 5px;\n		a{\n			opacity: .7;\n			&:hover{\n				opacity: 1;\n			}\n			img{\n				max-width: 45px;\n			}\n		}\n	}\n}\n\n	\n\n\n.ibox{\n	text-align: center;\n	.ibox-text h4{\n		margin-bottom: 5px;\n		line-height: 1.4em;\n	}\n	.ibox-desc{\n\n		.ibox-link{\n			display: block;\n			margin-top: .5em;\n			i{\n				.transition(all 0.2s ease-in-out);\n\n			}\n			a:hover i{\n				margin-left: 6px;\n			}\n		}\n	}\n	\n	&:hover .ibox-icon-border{\n		//.scale( 1.1 );\n		//.translate3d(0, 5px, 0);\n		\n	}\n\n	.ibox-media{\n		text-align: center;\n		a{color: inherit;}\n		.pl-counter{\n			font-size: 80px;\n			font-weight: 600;\n			letter-spacing: -1px;\n			line-height: 1em;\n		}\n		.ibox-icon-border{\n			margin-bottom: 12px;\n			display: inline-block;\n			text-align: center;\n			.transition(all 0.2s ease-in-out);\n		\n			border-radius: 500px;\n			position: relative;\n			.invert-icon{\n				position: absolute; \n				width: 100%; \n				height: 100%;\n				border-radius: 500px;\n				z-index: 0;\n				left: 0;\n				top: 0;\n				opacity: 0;\n				.transition(all 0.2s ease-in-out);\n			}\n			.iii{\n				position: relative; \n				z-index: 10;\n			}\n			\n			&.media-type-image {\n				overflow: hidden;\n				\n				img{\n					border-radius: 500px;\n				}\n				background-size: cover;\n				display: block;\n				margin-right: auto;\n				margin-left: auto;\n			}\n			&.media-type-icon {\n		\n				i{\n					font-size: 80px;\n					display: inline-block;\n				}\n				\n			}\n		}\n		\n	}\n	\n		\n	&.top-aligned{\n		.ibox-desc{\n			max-width: 300px;\n			margin: 0 auto;\n		}\n		.ibox-media{\n			max-width: 70%;\n			&.wrap-count{\n				max-width: 100%;\n			}\n			margin-right: auto;\n			margin-left: auto;\n			.media-type-image {\n			\n				height: 0; \n				padding: 50% 0;\n			}\n			\n		}\n		\n	}\n	&.left-aligned{\n		text-align: left;\n		.ibox-icon-border{\n			.pl-counter{\n				font-size: 25px;\n				line-height: 1em;\n			}\n			&.media-type-count{\n				min-width: 100px;\n				text-align: right;\n			}\n			&.media-type-icon {\n				background: transparent;\n				width: 40px;\n				height: 40px;\n				line-height: 40px;\n				i{\n					font-size: 30px;\n				}\n\n			}\n			&.media-type-image {\n				padding: auto;\n				width: 80px;\n				height: 80px;\n				img{\n					width: 80px;\n				}\n\n			}\n		}\n	}\n}\n\n\n\n.section-postauthor{\n	margin-bottom: 2em;\n	text-align: left;\n	.copy-pad {\n		padding:  10px 15px;\n	}\n	.author-note{\n		.noteText();\n	}\n	.subtext{\n		font-size: 86%;\n		color: @textColorAlt;\n	}\n}\n.section-textbox {\n\n	.pl-section-pad{\n	\n		.textbox-wrap p:last-child{\n			margin-bottom: 0;\n		}\n		.textbox-wrap p:first-child{\n			margin-top: 0;\n		}\n	}\n}.pl-new-loop .metahead-page{\n	padding-left: 100px;\n}\n\n.pl-new-loop{\n	.hentry{\n		position: relative;\n\n\n		&.metahead-page{\n			padding-left: 100px;\n		}\n		&.single-post .metamedia{\n			margin: 25px 0;\n		}\n\n		.continue_reading_link{\n			margin-top: 20px;\n		}\n\n		.entry-title{\n			margin:0;\n		}\n\n		.metamedia{\n			margin-bottom: 15px;\n\n		}\n		.metabar{\n			font-size: 85%;\n			line-height: 1.6em;\n			opacity: .7;\n			margin: 5px 0 15px;\n		}\n\n		.entry_content{\n			img {\n				max-width: 100%;\n			}\n			> :first-child{\n				margin-top: 0;\n			}\n			> :last-child{\n				margin-bottom: 0;\n			}\n		}\n\n	}\n}\n\n\n.metahead{\n\n	width: 70px;\n	position: absolute;\n	left: 0;\n	top: 0;\n	font-size: 85%;\n	line-height: 1.2em;\n	.metaicon{\n		float: right;\n		margin-top: 6px;\n	}\n	.author{\n		font-weight: 600;\n		border-style: solid;\n		border-bottom-width: 1px;\n		display: block;\n		margin-bottom: 10px;\n	}\n	a{\n		color: inherit;\n\n		display: block;\n		text-align: right;\n		margin-bottom: 8px;\n		.avatar{\n			max-width: 50px;\n		}\n		&:last-child{\n			border-bottom: none;\n		}\n	}\n	\n}\n@media (max-width: 600px){\n	.pl-new-loop .hentry{\n		&.post,\n		&.multi-post{\n			padding-left: 0px;\n		}\n	}\n	\n	.metahead{\n		position: relative; \n		width: 100%;\n		margin-bottom: 10px;\n		a, .author{\n			text-align: left; \n			display: inline-block;\n			border: none;\n			margin: 0 5px 0;\n			font-size: 16px;\n		}\n		.pl-social-pagelines{\n			text-align: right;\n			position: absolute; \n			right: 0;\n			top: 50%;\n			margin-top: -8px;\n		}\n	}\n	\n}\n\n.multi-post {\n	.pl-new-loop{\n		.hentry {\n			border-bottom-style: solid;\n			border-bottom-width: 1px;\n			padding-bottom: 50px;\n			margin-bottom: 50px;\n		}\n	}\n	.pl-old-loop{\n		\n		\n			.hentry {\n				padding-bottom: 15px;\n				margin-bottom: 15px;\n				\n			}\n\n			.clip_box{\n				margin: 0 0 @baseLineHeight;\n				border-color: @dark-base;\n				.clip{\n					width:47%;\n					clear: none;\n					float: left;\n					margin: 0;\n					.hentry-pad{\n						padding: 0 0 @baseLineHeight/2;\n					}\n					&.clip-right{\n						float: right;\n					}\n					.clip-header {\n						padding: 4px 0 4px 0;\n						display: table-cell;\n						width: 10000px;\n					}\n					.clip-thumb{\n						a{\n							float:left;\n							margin-right: 10px;\n						}\n						img{\n							padding: 2px;\n							display:block;\n							margin:0 0 0.4em;\n						}\n					}\n\n					.post-excerpt{\n						font-size: 90%;\n						line-height: 1.6em;\n						margin-bottom:1.5em;\n					}\n\n					.entry-title,\