plate unable to select\n		https://github.com/pagelines/DMS/issues/784\n	*/\n	.panel-page-setup .tab-panel-inner {\n	    margin-top: 20px;\n	}	\n\n}\n\n\n\n// Drag and Drop Editing\n// --------------------------------------------------\n\n// Used to disable text selection, used in resize.. and more\n.disable-select{\n	.user-select(none);\n}\n\n.btn-region,\n.pl-section-controls{\n	font-family: @defaultFont;\n}\n\n.drag-drop-editing{\n\n	// Area Controls\n	// --------------------------------------------------\n\n	.pl-editor-only{\n		display: block;\n	}\n	.blank-section-template{\n		text-align: center; \n		font-size: 12px;\n		padding: 5px;\n	}\n	.pl-region{\n		position: relative;	\n		.pl-region-bar{\n			display: block;\n			text-align: center;\n			z-index: @zindexEditor;\n			background: rgba(125, 233, 253, .1);\n			border-top: 1px dashed @editorBorder;\n			width: 100%;\n			height: 10px;\n			line-height: 10px;\n			a.btn-region{\n				text-transform: uppercase;\n				background: rgba(125, 233, 253, .6);\n				color: rgba(0,0,0,.8);\n				text-shadow: 0 1px 0 rgba(255,255,255,.4);\n				font-size: 8px;\n				line-height: 10px;\n				padding: 0px 4px;\n				font-weight: 600;\n				vertical-align: top;\n				cursor: pointer;\n				i{\n					vertical-align: top;\n					padding-top: 1px;\n				}\n			}\n		}\n		\n	}\n	.custom-template,\n	.custom-section{\n		.pl-region-bar{\n			background: rgba(255, 179, 51, .1);\n			border-top: 1px dashed #ffb333;\n			a.btn-region{\n				background: rgba(255, 179, 51, .5);\n				&:hover{\n					background: rgba(255, 179, 51, .8);\n				}\n			}\n		}\n		.pl-content.nested-section-area {\n			border-top-color: #ffb333;\n			border-bottom-color: #ffb333;\n		}\n	 	.pl-area{\n			border-top-color: #ffb333;\n		}\n		\n		.ui-resizable-w,\n		.ui-resizable-e{\n			border-color: #ffb333;\n		}\n		.pl-section-title{\n			border-color: #ffb333;\n		}\n	}\n\n	.pl-area-container{\n		min-height: 20px;\n	}\n	.pl-area-ui-element{\n		border-bottom: 1px dashed @editorBorder;\n	}\n	.pl-area-placeholder{\n		max-height: 400px;\n		border-top: 1px dashed @editorBorder;\n		#gradient > .vertical(rgba(182, 242, 253, .06), rgba(125, 233, 253, .06));\n	}\n\n	.pl-area{\n		position: relative;\n		border-top: 1px dashed @editorBorder;\n		&.ui-sortable-helper{\n			border: 1px dashed @editorBorder;\n			.pl-area-controls{\n				display: none;\n			}\n		}\n		.pl-area-controls{\n			.transition(all 0.15s ease-in-out);\n			display: block;\n			z-index: @zindexEditor;\n			position: absolute;\n			left: 0;\n			top: 4px;\n			text-align: right;\n			width: 91px;\n			line-height: 1em;\n			font-size: 9px;\n\n			background: @interfaceBG;\n			text-shadow: 0 -1px 0 @interfaceBG;\n			color: #fff;\n			margin-left: -74px;\n\n			\n			&:hover,\n			&.area-control-hover{\n				opacity: 1;\n				margin-left: 0;\n				z-index: @zindexTooltip;\n			}\n			&::after{\n				content: '''';\n				display: block;\n				width: 200px;\n				height: 160%;\n				position: absolute;\n				top: -5%;\n				right: -50px;\n				z-index: -100;\n			}\n\n\n			.area-control{\n				display: inline-block;\n				font-size: 10px;\n				font-weight: 600;\n				text-shadow: 0 -1px 0 @interfaceBG;\n				color: #fff;\n				height: 17px;\n				width: 17px;\n				line-height: 17px;\n				text-align: center;\n				&.area-reorder:hover{\n					cursor: move;\n				}\n				&:hover{\n					\n					cursor:pointer;\n					background:rgba(0,0,0,.6);\n				}\n				&:active{\n					background:rgba(0,0,0,1);\n				}\n			}\n			.area-unlock,\n			.tpl-unlock{\n				display: none;\n			}\n		}\n	}\n\n	&.display-boxed .page-canvas .pl-area {\n		overflow-x: hidden;\n		.pl-area-controls{\n\n		//	margin-left: 0;\n\n			&:hover{\n				opacity: 1;\n			//	margin-left: -92px;\n			}\n		}\n	}\n\n	.pl-section{\n		.box-sizing(border-box);\n		position: relative;\n		.transition(all 0.1s linear);\n		&.section-hover,\n		&:hover,\n		&.ui-sortable-helper {\n			\n			cursor: move;\n			box-shadow: 0 1px 3px rgba(0,0,0,.3);\n		//	background-color: rgba(0,0,0,.05);\n			> .pl-section-controls{\n				display: block;\n			}\n		}\n		\n		&.ui-sortable-helper{\n			.transition(none);\n			z-index: 10000;\n		}\n\n\n		&.section-plcolumn{\n			&:hover{background: transparent;}\n			margin-bottom: 20px;\n			outline: 1px dashed #35eafd;\n\n			&.empty-area .pl-sortable-column{\n				min-height: 70px;\n				.pl-column-forcer{\n					display: block;\n					width: 0;\n					height: 0;\n				}\n\n			}\n			&.force-start-row > .pl-section-controls:first-child a.s-control.section-start-row{\n				background:@editorBorder;\n			}\n			> .pl-section-controls:first-child{\n				top: -20px;\n				#gradient > .vertical(rgba(182, 242, 253, .8), rgba(125, 233, 253, .8));\n				&:hover{\n					#gradient > .vertical(rgba(182, 242, 253, .9), rgba(125, 233, 253, .9));\n				}\n				box-shadow: inset 1px 1px 0 rgba(255,255,255,.5);\n				outline: 1px dashed  @editorBorder;\n				color: @editorText;\n				text-shadow: 0 1px 0 rgba(255,255,255,.4);\n\n				a.s-control{\n					color: @editorText;\n					background: rgba(196, 235, 242, .4);\n					&:hover{\n						background:@editorBorder;\n						color:#000;\n					}\n					&:active{\n						background-color: #01c5df;\n						color:#222;\n					}\n				}\n\n			}\n\n		}\n		// Section Controls\n		.pl-section-controls{\n			position: absolute;\n			top: 0;\n			left: 0;\n			width: 100%;\n			z-index: @zindexEditor;\n			background: @interfaceBG;\n			text-shadow: 0 -1px 0 @interfaceBG;\n			color: #fff;\n			text-align: center;\n			\n			font-weight: 600;\n			font-size: 10px;\n			line-height: 21px;\n			.box-sizing(border-box);\n			&.section-controls-footer{\n				bottom: 0;\n				top: auto;\n				background: transparent;\n				a.s-control{\n					background: @interfaceBG;\n				}\n			}\n			&:hover{\n				cursor: move;\n			}\n			.controls-title,\n			.controls-right,\n			.controls-left{\n				display: inline;\n				clear: none;\n			}\n			.title-desc{\n				color: rgba(255,255,255, .5);\n			}\n			.controls-right{\n				padding-right: 4px;\n				float: right;\n			}\n			.controls-left{\n				padding-left: 4px;\n				float: left;\n			}\n			.s-control{\n				line-height: 16px;\n\n				font-size: 11px;\n				display: inline-block;\n				text-align: center;\n			}\n			span.s-control{\n				letter-spacing: .1em;\n			}\n			a.s-control{\n\n				width: 16px;\n				height: 16px;\n\n				padding: 0 1px;\n\n				.border-radius(3px);\n				background: rgba(0,0,0,.3);\n				&:hover{\n					background: rgba(0,0,0,.6);\n					color:#eee;\n				}\n				&:active{\n					background: rgba(0,0,0,.4);\n					color:#ccc;\n				}\n				.caret, &:hover .caret{\n				  border-top:   4px solid #fff;\n				  opacity: .6;\n				  content: "";\n				}\n				&.s-control-icon{\n					width: 16px;\n					height: 16px;\n					color:#fff;\n				}\n			}\n\n\n		}\n		\n	}\n	\n	\n	.editing-locked{\n		&.pl-region{\n			.linked-tpl{\n				display: inline;\n			}\n			.tpl-unlock{\n				display: inline-block;\n				opacity: .7;\n			}\n		}\n		&.pl-area{\n			.linked-section{\n				display: inline;\n			}\n			.area-unlock{\n				display: inline-block;\n				\n			}\n			.area-save{\n				display: none;\n			}\n		}\n	\n		\n		\n	}\n\n	.pl-sortable-area,\n	.pl-sortable-column{\n		min-height: 35px;\n		.transition(.5s);\n	}\n	&.pl-dragging{\n		.pl-sortable-column{\n			padding-top: 0px;\n		}\n		.section-plcolumn{\n			background: @pl-base;\n			outline: 1px dashed #35eafd;\n		}\n		.ui-sortable-helper{\n			border: 1px solid rgba(255,255,255,.4);\n			box-shadow: 0 1px 4px rgba(0,0,0,.5);\n			max-height: 350px;\n			overflow:hidden;\n			opacity: .5;\n		}\n		.pl-sortable-buffer{\n			height: 5px;\n			margin: 5px 0;\n			display: block;\n		}\n		&:hover,\n		&.ui-sortable-helper,\n		.section-plcolumn {\n			.pl-section-controls{\n				display: none !important;\n			}\n		}\n\n	}\n\n\n	// Area for dropping elements on drag\n	.pl-placeholder {\n		max-height: 200px;\n		display: inline;\n		float: left;\n		clear: none;\n		position: relative;\n		margin: 0 0 1em 0;\n		padding: 0;\n		.box-sizing(border-box);\n		border-radius: 5px;\n		#gradient > .vertical(rgba(182, 242, 253, .2), rgb