 2;\n}\n\n.help-inline {\n  display: inline-block;\n  .ie7-inline-block();\n  vertical-align: middle;\n  padding-left: 5px;\n}\n\n\n\n// INPUT GROUPS\n// ------------\n\n// Allow us to put symbols and text within the input field for a cleaner look\n.input-append,\n.input-prepend {\n  margin-bottom: 5px;\n  font-size: 0;\n  white-space: nowrap; // Prevent span and input from separating\n\n  input,\n  select,\n  .uneditable-input {\n    position: relative; // placed here by default so that on :focus we can place the input above the .add-on for full border and box-shadow goodness\n    margin-bottom: 0; // prevent bottom margin from screwing up alignment in stacked forms\n    *margin-left: 0;\n    font-size: @baseFontSize;\n    vertical-align: top;\n    .border-radius(0 @inputBorderRadius @inputBorderRadius 0);\n    // Make input on top when focused so blue border and shadow always show\n    &:focus {\n      z-index: 2;\n    }\n  }\n  .add-on {\n    display: inline-block;\n    width: auto;\n    height: @inputHeight;\n    min-width: 16px;\n    padding: 4px 5px;\n    font-size: @baseFontSize;\n    font-weight: normal;\n    line-height: @baseLineHeight;\n    text-align: center;\n    text-shadow: 0 1px 0 @white;\n    background-color: @grayLighter;\n    border: 1px solid #ccc;\n  }\n  .add-on,\n  .btn {\n    vertical-align: top;\n    .border-radius(0);\n  }\n  .active {\n    background-color: lighten(@green, 30);\n    border-color: @green;\n  }\n}\n.input-prepend {\n  .add-on,\n  .btn {\n    margin-right: -1px;\n  }\n  .add-on:first-child,\n  .btn:first-child {\n    .border-radius(@inputBorderRadius 0 0 @inputBorderRadius);\n  }\n}\n.input-append {\n  input,\n  select,\n  .uneditable-input {\n    .border-radius(@inputBorderRadius 0 0 @inputBorderRadius);\n  }\n  .add-on,\n  .btn {\n    margin-left: -1px;\n  }\n  .add-on:last-child,\n  .btn:last-child {\n    .border-radius(0 @inputBorderRadius @inputBorderRadius 0);\n  }\n}\n// Remove all border-radius for inputs with both prepend and append\n.input-prepend.input-append {\n  input,\n  select,\n  .uneditable-input {\n    .border-radius(0);\n  }\n  .add-on:first-child,\n  .btn:first-child {\n    margin-right: -1px;\n    .border-radius(@inputBorderRadius 0 0 @inputBorderRadius);\n  }\n  .add-on:last-child,\n  .btn:last-child {\n    margin-left: -1px;\n    .border-radius(0 @inputBorderRadius @inputBorderRadius 0);\n  }\n}\n\n\n\n// SEARCH FORM\n// -----------\n\ninput.search-query {\n  padding-right: 14px;\n  padding-right: 4px \\9;\n  padding-left: 14px;\n  padding-left: 4px \\9; /* IE7-8 doesn''t have border-radius, so don''t indent the padding */\n  margin-bottom: 0; // Remove the default margin on all inputs\n  .border-radius(15px);\n}\n\n/* Allow for input prepend/append in search forms */\n.form-search .input-append .search-query,\n.form-search .input-prepend .search-query {\n  .border-radius(0); // Override due to specificity\n}\n.form-search .input-append .search-query {\n  .border-radius(14px 0 0 14px);\n}\n.form-search .input-append .btn {\n  .border-radius(0 14px 14px 0);\n}\n.form-search .input-prepend .search-query {\n  .border-radius(0 14px 14px 0);\n}\n.form-search .input-prepend .btn {\n  .border-radius(14px 0 0 14px);\n}\n\n\n\n\n// HORIZONTAL & VERTICAL FORMS\n// ---------------------------\n\n// Common properties\n// -----------------\n\n.form-search,\n.form-inline,\n.form-horizontal {\n  input,\n  textarea,\n  select,\n  .help-inline,\n  .uneditable-input,\n  .input-prepend,\n  .input-append {\n    display: inline-block;\n    .ie7-inline-block();\n    margin-bottom: 0;\n    vertical-align: middle;\n  }\n  // Re-hide hidden elements due to specifity\n  .hide {\n    display: none;\n  }\n}\n.form-search label,\n.form-inline label,\n.form-search .btn-group,\n.form-inline .btn-group {\n  display: inline-block;\n}\n// Remove margin for input-prepend/-append\n.form-search .input-append,\n.form-inline .input-append,\n.form-search .input-prepend,\n.form-inline .input-prepend {\n  margin-bottom: 0;\n}\n// Inline checkbox/radio labels (remove padding on left)\n.form-search .radio,\n.form-search .checkbox,\n.form-inline .radio,\n.form-inline .checkbox {\n  padding-left: 0;\n  margin-bottom: 0;\n  vertical-align: middle;\n}\n// Remove float and margin, set to inline-block\n.form-search .radio input[type="radio"],\n.form-search .checkbox input[type="checkbox"],\n.form-inline .radio input[type="radio"],\n.form-inline .checkbox input[type="checkbox"] {\n  float: left;\n  margin-right: 3px;\n  margin-left: 0;\n}\n\n\n// Margin to space out fieldsets\n.control-group {\n  margin-bottom: @baseLineHeight / 2;\n}\n\n// Legend collapses margin, so next element is responsible for spacing\nlegend + .control-group {\n  margin-top: @baseLineHeight;\n  -webkit-margin-top-collapse: separate;\n}\n\n// Horizontal-specific styles\n// --------------------------\n\n.form-horizontal {\n  // Increase spacing between groups\n  .control-group {\n    margin-bottom: @baseLineHeight;\n    .clearfix();\n  }\n  // Float the labels left\n  .control-label {\n    float: left;\n    width: @horizontalComponentOffset - 20;\n    padding-top: 5px;\n    text-align: right;\n  }\n  // Move over all input controls and content\n  .controls {\n    // Super jank IE7 fix to ensure the inputs in .input-append and input-prepend\n    // don''t inherit the margin of the parent, in this case .controls\n    *display: inline-block;\n    *padding-left: 20px;\n    margin-left: @horizontalComponentOffset;\n    *margin-left: 0;\n    &:first-child {\n      *padding-left: @horizontalComponentOffset;\n    }\n  }\n  // Remove bottom margin on block level help text since that''s accounted for on .control-group\n  .help-block {\n    margin-bottom: 0;\n  }\n  // And apply it only to .help-block instances that follow a form control\n  input,\n  select,\n  textarea {\n    + .help-block {\n      margin-top: @baseLineHeight / 2;\n    }\n  }\n  // Move over buttons in .form-actions to align with .controls\n  .form-actions {\n    padding-left: @horizontalComponentOffset;\n  }\n}\n\n// ---------------------------------------------------------------\n// PageLines Styles\n// ---------------------------------------------------------------\n\n/* Placeholder */\n.placeholder();\n\n.pl-searcher{\n	cursor: pointer;\n	input::-webkit-input-placeholder { text-transform: uppercase; letter-spacing: 1px; color: rgba(0,0,0,.2); }\n	input::-moz-placeholder { text-transform: uppercase; letter-spacing: 1px; color: rgba(0,0,0,.2); }\n}\n/* @ Search Form */\n.searchform {\n	position: relative;\n//	width: 190px;\n	margin: 3px 0 3px 0;\n	display: inline-block;\n	input::-webkit-input-placeholder { text-transform: uppercase; letter-spacing: 1px; }\n	input::-moz-placeholder { text-transform: uppercase; }\n	.btn-search{\n		position: absolute;\n		z-index: 10;\n		left: 5px;\n		top: 1px;\n		color: #000;\n		&:hover{\n			cursor: pointer;\n			opacity: .6;\n		}\n	}\n	.searchfield{\n		.border-radius( 3px);\n		height: 23px;\n		padding: 5px 5px 5px 12px;\n		margin: 0;\n		cursor: default;\n		\n		font-family: @buttonFontFamily;\n		font-size: 11px;\n		line-height: 1.2em;\n		.transition(all .7s);\n		text-indent: 15px;\n		box-sizing: border-box;\n		width: 100%;\n		&:focus{\n			cursor: text;\n			box-shadow: none;\n			\n			background-color:#fff;\n			color:#000;\n			\n		}\n	}\n\n}\n\n\n.billboard .searchfield,\n.boomboard .searchfield{\n	float: none;\n}\n\n.navigation_wrap .searchform {\n	position: absolute;\n	right: 3px;\n	bottom: 2px;\n}\n\n\n\n// BUTTON INPUTS\n// -------------------\ninput[type="submit"],\n.submit,\ninput[type="button"],\n.button,\ninput[type="reset"],\n.reset{\n	vertical-align: top;\n	padding: 5px 9px;\n	cursor: pointer;\n	text-decoration: none;\n	.box-sizing(content-box);\n	.border-radius(2px);\n	color: @pl-text;;\n	border:1px solid darken(@baseContrastSlight, 20%);\n	background:  @baseContrastSlight;\n	\n	font-family: @defaultFont;\n	&:hover{\n		opacity: .8;\n	}\n	&:active {\n		box-shadow: inset 0 2px 2px rgba(0,0,0,.15), 0 1px 2px rgba(0,0,0,.05);\n		background-color: darken(@baseContrast, 5%);\n		outline: 0;\n	}\n	\n}\n\ninput[type="image"]{\n	box-shadow: none;\n}\n\n\n//\n// Breadcrumbs\n// --------------------------------------------------\n\n\n.breadcrumb {\n  padding: 8px 15px;\n  margin: 0 0 @baseLineHeight;\n  list-style: none;\n  background-color: #f5f5f5;\n  border-radius: 4px;\n  li {\n    display: inline-block;\n    .ie7-inline-block();\n    text-shadow: 0 1px 0 @white;\n  }\n  .divider {\n    padding: 0 5px;\n    color: #ccc;\n  }\n  .active {\n    color: @grayLight;\n  }\n}\n//\n// Close icons\n// --------------------------------------------------\n\n\n.close {\n  float: right;\n  font-size: 20px;\n  font-weight: bold;\n  line-height: @baseLineHeight;\n  color: @black;\n  text-shadow: 0 1px 0 rgba(255,255,255,1);\n  opacity: .2;\n  &:hover {\n    color: @black;\n    text-decoration: none;\n    cursor: pointer;\n    opacity: .4;\n  }\n}\n\n// Additional properties for button version\n// iOS requires the button element instead of an anchor tag.\n// If you want the anchor version, it requires `href="#"`.\nbutton.close {\n  padding: 0;\n  cursor: pointer;\n  background: transparent;\n  border: 0;\n  -webkit-appearance: none;\n}// --------------------------------------------------\n// PageLines Pager\n// --------------------------------------------------\n\n\n.pager {\n  list-style: none;\n  text-align: center;\n  .clearfix();\n	li {\n	  display: inline;\n	}\n	a,\n	span {\n		display: inline-block;\n		padding: 5px 14px;\n		background: @baseContrastSlight;\n		border: 1px solid @baseContrastDark;\n		.border-radius(15px);\n	}\n	a:hover {\n	  text-decoration: none;\n	  background-color: @baseContrastMore;\n	}\n	a:active{\n		.box-shadow(~"1px 1px 0 @baseLighterMore, inset 1px 1px 0 rgba(0,0,0,.2)");\n		background-color: @baseContrastDark;\n		border: 1px solid @baseContrastDarkMore;\n	}\n	.next{\n		a, span{\n			float: right;\n		}\n	}\n	.previous a {\n